{
    int i2c_master_port = I2C_MASTTER_NUM;
    i2c_config_t conf = {
        .mode = I2C_MODE_MASTER,
        .sda_io_num = I2C_MASTER_SDA_IO,
        .sda_pullup_en = GPIO_PULLUP_ENABLE,
        .scl_io_num = I2C_MASTER_SCL_IO,
        .scl_pullup_en = GPIO_PULLUP_ENABLE,
        .master.clk_speed = I2C_MASTER_FREQ_HZ,
        .clk_flags = 0,          /*!< Optional, you can use I2C_SCLK_SRC_FLAG_* flags to choose i2c source clock here. */
    };

    esp_err_t err = i2c_param_config(i2c_master_port, &conf);
    if (err != ESP_OK) {
        return err;
    }
    return i2c_driver_install(i2c_master_port, conf.mode, I2C_MASTER_RX_BUF_DISABLE, I2C_MASTER_TX_BUF_DISABLE, 0);
}

void app_main(void)
{
    ESP_ERROR_CHECK(i2c_master_init());
    ssd1306_init();
    ESP_LOGI(TAG, "Oled init");
    // task_ssd1306_display_img_hrz_mode(imgUIT);
    xTaskCreate(&task_ssd1306_display_img, "ssd1306_display_img", 2048, imgUIT, 6, NULL);
    // xTaskCreate(&task_ssd1306_display_text, "ssd1306_text", 2048, "hello\nuit", 6, NULL);
}